package BasicPrograms;

public class SwapNumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
         int a=10;
         int b=30;
         
         b=a+b;
         a=b-a;
         b=b-a;
         
         System.out.println("Swap numbers are:"+"a:"+a +"and b:" +b);
	}

}
